package es.avalon;

public class Principal10buclewhile {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		// bucle while
		
		int x=1;
		
		while (x<10) {
			
			System.out.println(x);
			
			x=x+1;
		}
		
		
		// bucle do while
		
		int n=1;
		
		do {
			
			System.out.println(n);
			n=n+1;
			
		} while (n<10);
		
	}

}
