package es.avalon;

public class Principal17funcionlistado {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// definimos una funcion que devuelva la lista de numeros hasta el numero introducido
		
		listado(20);
	}

	private static void listado(int x) {
		
		for(int i=0; i<x; i++) {
			
			System.out.println(i);
		}
	}

}
