package es.avalon;

public class Principal5buclesuma {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//suma de los 10 primeros numeros
		
		int suma=0;
				
		for(int i=0; i<10; i=i+1) {
			
			suma=suma+i;			
			
			System.out.println(i);
			System.out.println(suma); }
		

	}

}
