package es.avalon;

public class Principal7escalonadoSTRING {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// mostrar los numeros del 1 al 10 de forma escalonada
		
		String espacio=""; // string para trabajar con cadenas de texto
				
		for(int i=0; i<=10; i++) {
			
			espacio=espacio+" ";
			
			System.out.println(espacio+i);
			
		}
		
	}

}

